<?php

declare(strict_types=1);

namespace Packeton\Repository;

use Packeton\Entity\Group;
use Packeton\Entity\GroupAclPermission;
use Packeton\Entity\Package;
use Packeton\Entity\User;
use Packeton\Model\PacketonUserInterface;
use Symfony\Component\Security\Core\User\UserInterface;

/**
 * GruopRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class GroupRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * @param UserInterface $user
     * @param Package $package
     *
     * @return array
     */
    public function getAllowedVersionByPackage(?UserInterface $user, Package $package)
    {
        $qb = $this->getEntityManager()->createQueryBuilder();
        if ($package->isFullVisibility()) {
            return [null]; // all versions
        }

        if ($user instanceof User) {
            $qb
                ->select('acl.version')
                ->distinct()
                ->from(User::class, 'u')
                ->innerJoin('u.groups', 'g')
                ->innerJoin('g.aclPermissions', 'acl')
                ->innerJoin('acl.package', 'p')
                ->where('u.id = :uid')
                ->andWhere('p.id = :pid')
                ->setParameter('pid', $package->getId())
                ->setParameter('uid', $user->getId());
        } else if ($user instanceof PacketonUserInterface) {
            $qb->select('acl.version')
                ->distinct()
                ->from(GroupAclPermission::class, 'acl')
                ->where('IDENTITY(acl.package) = :pid')
                ->andWhere('IDENTITY(acl.group) IN (:gid)')
                ->setParameter('pid', $package->getId())
                ->setParameter('gid', $user->getAclGroups() ? : [-1]);
        } else {
            return [];
        }

        $result = $qb->getQuery()->getResult();
        if ($result) {
            $result = \array_column($result, 'version');
        }

        return $result;
    }

    public function getExpirationDateForUser(PacketonUserInterface $user): array
    {
        $qb = $this->getEntityManager()->createQueryBuilder();

        $result = [];
        $expiredData = $qb->select('MAX(g.expiredUpdatesAt) as expired', 'IDENTITY(perm.package) as package')
            ->from(Group::class, 'g')
            ->innerJoin('g.aclPermissions', 'perm')
            ->andWhere('g.expiredUpdatesAt IS NOT NULL')
            ->andWhere('g.id IN (:ids)')
            ->groupBy('package')
            ->setParameter('ids', $user->getAclGroups() ?: [-1])
            ->getQuery()->getArrayResult();

        foreach ($expiredData as $item) {
            $result[(int)$item['package']] = new \DateTimeImmutable($item['expired'], new \DateTimeZone('UTC'));
        }

        return $result;
    }

    /**
     * @param UserInterface $user
     * @param bool $hydration flags
     *
     * @return Package[]
     */
    public function getAllowedPackagesForUser(?UserInterface $user, bool|int $hydration = true)
    {
        if (!$user instanceof PacketonUserInterface) {
            return [];
        }

        $qb = $this->getEntityManager()->createQueryBuilder();
        $qb->distinct();

        if ($user instanceof User) {
            $qb
                ->select('p.id')
                ->from(User::class, 'u')
                ->innerJoin('u.groups', 'g')
                ->innerJoin('g.aclPermissions', 'acl')
                ->innerJoin('acl.package', 'p')
                ->where('u.id = :uid')
                ->setParameter('uid', $user->getId());
        } else {
            $qb
                ->select('IDENTITY(acl.package) as id')
                ->from(GroupAclPermission::class, 'acl')
                ->andWhere('IDENTITY(acl.group) IN (:gid)')
                ->setParameter('gid', $user->getAclGroups() ? : [-1]);
        }

        $result = $qb->getQuery()->getSingleColumnResult();
        $result = array_merge($result, $this->getFullVisiblePackages());

        if (empty($result)) {
            return [];
        }

        if (true === $hydration) {
            $qb = $this->getEntityManager()->createQueryBuilder();
            $qb->select('p')
                ->from(Package::class, 'p')
                ->where('p.id IN (:ids)')
                ->setParameter('ids', $result);

            return $qb->getQuery()->getResult();
        }
        $hydration = (int)$hydration;
        if ($hydration & 1) {
            $qb = $this->getEntityManager()->createQueryBuilder();
            $qb->select('p.name')
                ->from(Package::class, 'p')
                ->where('p.id IN (:ids)')
                ->setParameter('ids', $result);
            return $qb->getQuery()->getSingleColumnResult();
        }

        return $result;
    }

    public function getFullVisiblePackages(): array
    {
        $qb = $this->getEntityManager()->createQueryBuilder();

        return $qb->select('p.id')
            ->from(Package::class, 'p')
            ->where('p.fullVisibility = :visibility')
            ->setParameter('visibility', true)
            ->getQuery()->getSingleColumnResult();
    }

    public function getGroupsData(Group|int $group): array
    {
        $qb = $this->getEntityManager()->createQueryBuilder();

        $result = $qb
            ->select('p.name')
            ->from(Group::class, 'g')
            ->innerJoin('g.aclPermissions', 'acl')
            ->innerJoin('acl.package', 'p')
            ->where('g.id = :gid')
            ->setParameter('gid', $group instanceof Group ? $group->getId() : $group)
            ->getQuery()
            ->getArrayResult();

        $packages = array_column($result, 'name');

        return ['packages' => $packages];
    }

    public function getApiData(Group $group)
    {
        return [
            'id' => $group->getId(),
            'name' => $group->getName(),
            'proxies' => $group->getProxies(),
            'expiredUpdatesAt' => $group->getExpiredUpdatesAt(),
        ] + $this->getGroupsData($group);
    }

    public function getAllowedProxies(?UserInterface $user)
    {
        if (!$user instanceof PacketonUserInterface) {
            return [];
        }

        if ($user instanceof User) {
            $qb = $this->getEntityManager()->createQueryBuilder()
                ->select('g.proxies')
                ->from(User::class, 'u')
                ->innerJoin('u.groups', 'g')
                ->where('u.id = :uid')
                ->setParameter('uid', $user->getId());
        } else {
            $qb = $this->getEntityManager()->createQueryBuilder()
                ->select('g.proxies')
                ->from(Group::class, 'g')
                ->andWhere('g.id IN (:gid)')
                ->setParameter('gid', $user->getAclGroups() ? : [-1]);
        }

        $proxies = $qb->getQuery()
            ->getArrayResult();
        $proxies = array_column($proxies, 'proxies');

        return $proxies ? \array_unique(\array_merge(...$proxies)) : [];
    }
}
